const addEventCallback = require('addEventCallback');
const BigQuery = require('BigQuery');
const getClientName = require('getClientName');
const getEventData = require('getEventData');
const getAllEventData = require('getAllEventData');
const getTimestampMillis = require('getTimestampMillis');
const getType = require('getType');
const makeString = require('makeString');
const JSON = require('JSON');
const log = require('logToConsole');

const event_name = getEventData('event_name') || '';
const all_event_data = getAllEventData();
const timestamp = getTimestampMillis();

// Add the event callback to the container instance
addEventCallback((containerId, eventData) => {
  let row = {
    sgtm_timestamp: getTimestampMillis(),
    event: event_name,
    data: [], //JSON.stringify(all_event_data)
    context: []
  };

  for (var key in all_event_data) {
    switch (getType(all_event_data[key])) {
      case "string":
        row.data.push({"key":key, "value": all_event_data[key]});
        break;
      case "object":
        row.data.push({"key":key, "value": JSON.stringify(all_event_data[key])});
        break;
      case "array":
        // for each make key.0, key.1, ...
        for (var i = 0; i < all_event_data[key].length; i++) {
          var v = all_event_data[key][i];
          // TODO if value is object, then loop object keys and => key.0.nested_key
          switch(getType(v)) {
            case "object":
            case "array": 
              v = JSON.stringify(v);
              break;
            default:
              v = makeString(all_event_data[key]);
          }
          row.data.push({"key":key+"."+i, "value": v});
        }

        break;
      default:
        row.data.push({"key":key, "value": makeString(all_event_data[key])});
    }
  }
  const ContainerVersion = require('getContainerVersion')();
  const cv_keys = ['containerId', 'debugMode', 'environmentName', 'environmentMode', 'previewMode', 'version'];
  for (let i = 0; i < cv_keys.length; i++) {
    row.context.push({"key":"ContainerVersion."+cv_keys[i], "value": makeString(ContainerVersion[cv_keys[i]])});
  }

  row.context.push({"key":"getClientName", "value": makeString(getClientName())});
  row.context.push({"key":"getCookieValues: _ga", "value": JSON.stringify(require('getCookieValues')('_ga'))});
  row.context.push({"key":"getRemoteAddress", "value": require('getRemoteAddress')()});
//*
  row.context.push({"key":"getRequestHeader: user-agent", "value": require('getRequestHeader')('user-agent')});
  row.context.push({"key":"getRequestHeader: host", "value": require('getRequestHeader')('host')});
  row.context.push({"key":"getRequestHeader: true-client-ip", "value": require('getRequestHeader')('True-Client-Ip')});
  row.context.push({"key":"getRequestPath", "value": require('getRequestPath')()});
  row.context.push({"key":"getRequestQueryParameters", "value": JSON.stringify(require('getRequestQueryParameters')())});
  row.context.push({"key":"getRequestBody", "value": require('getRequestBody')()});
  row.context.push({"key":"getRequestMethod", "value": require('getRequestMethod')()});
/*
*/
  const rows = [row];

  BigQuery.insert({
    projectId: data.projectId,
    datasetId: data.datasetId,
    tableId: data.tableId,
  }, rows, {}, () => {
    log('BigQuery Success');
  }, (errors) => {
    log('BigQuery Failure');
    log(JSON.stringify(errors));
  });
});

// Always signal success, as the tag completes actually only after eventCallback.
data.gtmOnSuccess();

