const getRequestPath = require('getRequestPath');
const isRequestMpv1 = require('isRequestMpv1');
const extractEventsFromMpv1 = require('extractEventsFromMpv1');
const getRequestQueryParameter = require('getRequestQueryParameter');
const returnResponse = require('returnResponse');
const runContainer = require('runContainer');
const decodeUriComponent = require('decodeUriComponent');
const getType = require('getType');

const log = require('logToConsole');


const query_pa = getRequestQueryParameter('pa');
if (query_pa !== "purchase") {
  return;
}

const fixStrigns = function(obj) {
  for (var k in obj) {
    switch (getType(obj[k])) {
      case "string":
        obj[k] = obj[k].split("+").join(" ");
        break;
      case "object":
      case "array":
        obj[k] = fixStrigns(obj[k]);
        break;
    }
  }
  return obj;
};

const requestPath = getRequestPath();
if (requestPath === '/collect') {
  if (isRequestMpv1()) {
    require('claimRequest')();
    const events = extractEventsFromMpv1();
    for (let i = 0; i < events.length; ++i) {
      var event = fixStrigns(events[i]);
      runContainer(event);
    }
    returnResponse();
  }
}



