    if (getType(_ga_K748LN4XFE) === "string") {
      var sessionId = undefined;
      if (_ga_K748LN4XFE.indexOf('$') !== -1) {
        // New GA4 cookie format (e.g., GS2.1.s123456$o2$g1...)
        var parts = _ga_K748LN4XFE.split('.');
      if (parts.length >= 3) {
        var sessionParts = parts[2].split('$');
        if (sessionParts.length > 0) {
          sessionId = sessionParts[0].replace('s', '');
        }
      }
    } else {
      // Old GA4 cookie format (e.g., GS1.1.1234567890.2.0.1234567890.0.0.0)
      var parts = _ga_K748LN4XFE.split('.');
      if (parts.length >= 3) {
        sessionId = parts[2];
      }
    }

    if (getType(makeNumber(sessionId)) === "number") {
