// Init SGTM APIs
const logToConsole = require('logToConsole');
const getEventData = require('getEventData');
const JSON = require('JSON');
const sendHttpRequest = require('sendHttpRequest');
const getType = require('getType');

// hoists
const useGa4Items = data.useGa4Items || false;

// Custom log wrapper
function log(data, usePrefix){
    const logPrefix = 'SmartEmail: ';
    if(typeof(usePrefix) === 'undefined' || usePrefix === true) {
        logToConsole(logPrefix + data);
    } else {
        logToConsole(data);
    }
}

// Recursively merge two objects
function mergeObjects(obj1,obj2){
	var obj3 = {};
	for (var attrname in obj1) { obj3[attrname] = obj1[attrname]; }
	for (var attrname2 in obj2) { obj3[attrname2] = obj2[attrname2]; }
	return obj3;
}

// Custom product mapping
function getItemArrayOfObject(){
	const itemsGa4 = getEventData('items') || [];

    function getCustomItems(){
      const possibleOutputs = [
        data.customItems,
        //JSON.parse(data.customItems),
        getEventData(data.customItems),
        //JSON.parse(getEventData(data.customItems))
      ];

      for(const out in possibleOutputs){

        let outputType = getType(possibleOutputs[out]);
        log(outputType);
        if(outputType === 'array'){
          return possibleOutputs[out];
        }
      }
      return [];
    }

	return useGa4Items ? itemsGa4 : getCustomItems();
}

// Convert most array of object with key and value parameters names into key value object
function convertArrayToObject(arr) {
	const obj = {};
	arr.forEach(item => {
		obj[item.key] = item.value;
	});
	return obj;
}

function getConsentStatus(){
  const status = data.consentStatus;
  const agreementValues = [
    'granted',
    'Granted',
    'true',
    'True',
    true,
    'yes',
    'Yes'
  ];
  if(agreementValues.indexOf(status) !== -1){
    return true;
  } else {
    return false;
  }
}

// Payload getters
function getSentRecordTypeUID(){
   return data.eshopSentRecordTypeID || null;
}
function getSentRecordType(){
