}

function getEventType(){
	/* Supported event types ENUM
	 - order
	 - visit
	 - cart
	 - identify
	 - custom_event
	*/
	return data.eventType;
}

function getPayloadData(){
    log('Getting payload data');

    const items = getItemArrayOfObject();  
    const outputData = { 
        eshop_name: getEshopName(),
        eshop_code: getEshopCode(),
        status: getStatus(),
