}

function parseBool(boo){
  if(boo === true || boo === false){return boo;}
  else if(boo === 'true' || boo === 'True'  || boo === 1){return true;}
  else {return false;}
}

function getStatus(){
	return data.orderStatus;
}

function getEshopCode(){
    return data.eshopCode;
}

function getEshopName(){
	return data.eshopName;
}

function getIdentify(){
