		},
		reidentify: parseBool(data.identifyReidentify),
		update_existing: parseBool(data.identifyUpdateExisting)
	};
}

function getAttributes(){
	return data.customAttributes || [];
}

function getItems(items){
    // output array of objects 
    const itemList = []; 

    // Getters
	function getItemAttributes(item){
        log('Getting Item');
        log(item, false);
        const attributesArray = [];
        // map attributes object to output schema
        const overrideMap = convertArrayToObject(data.customItemAttributes || []);
        const attributes = createOverrideObj(item, overrideMap);
        log('Attributes: ------- ');
        log(attributes, false);
        for (var attr in attributes) {
            log('Item Attributes: ' + attr + ': ' + attributes[attr]);
            if (overrideMap.hasOwnProperty(attr)) {
                attributesArray.push({
                    name: attr,
                    value: attributes[attr]
                });
            }
        }
        return attributesArray;
	}

	function getItemQuantity(item){
		return item.quantity || 1;
	}

	function getItemCurrency(item){
		return item.price_currency || undefined;
	}

	function getItemPrice(item){
		return item.price_with_vat || 0;
	}

	function getItemPriceWithoutVAT(item){
		return item.price_without_vat || 0;
	}

	function getItemCode(item){
		return item.code || undefined;
	}

	function getItemName(item){
		return item.name;
	}

    // Main item loop
    for (const itemSource in items){
	  log('In main loop');
      log(items[itemSource], false);

      let ga4AutomaticMappings = {};

      // use ga4 native mapping
      if(useGa4Items){
        const ga4DynamicMapping = createOverrideObj(items[itemSource], {
            code: 'item_id',
            name: 'item_name',
            quantity: 'quantity'
        });

        ga4AutomaticMappings = mergeObjects(ga4DynamicMapping, {
			price_currency: getEventData('currency')
		});
      }

      // map item object to smartmailing output schema
      const overrideMap = convertArrayToObject(data.customItemMapping || []);
      const fieldsToEnhance = convertArrayToObject(data.customItemParametersDirect || []);

      // First, enhance items with ga4 mapping
      const automaticallyEnhancedItemsWithGa4Data = mergeObjects(items[itemSource], ga4AutomaticMappings);
      // Second, apply custom item level mappings
      const dynamicMappedItemsValue = createOverrideObj(automaticallyEnhancedItemsWithGa4Data, overrideMap);      
      // Third and last, map custom static values on item level parameters
      const item = mergeObjects(dynamicMappedItemsValue, fieldsToEnhance);
      // Propagate Item in SmartMailing Format
      itemList.push({
			 code: getItemCode(item),
			 name: getItemName(item),
			 price: { 
				with_vat: getItemPrice(item), 
				without_vat: getItemPriceWithoutVAT(item),
				currency: getItemCurrency(item)
			 },
			 quantity: getItemQuantity(item),
			 attributes: getItemAttributes(item)
		});
    }

	return itemList;
}

// Map keys from one object to another 
function createOverrideObj(itemObject, overrideMap){
	function overrideObject(originalKey, newKey, itemObject){
		if(itemObject[newKey]){
			return convertArrayToObject([{key: originalKey, value: itemObject[newKey]}]);
		} else {
			return {};
		}
	}
	let output = mergeObjects({}, itemObject);
	for (var key in overrideMap) {
		log('Reading map: ' + key  + ': ' + overrideMap[key]);
		if (overrideMap.hasOwnProperty(key)) {
			output = mergeObjects(output, overrideObject(key, overrideMap[key], itemObject));
		}
	}
	return output;
}

function modifyItemArray(){
	const itemMapping = data.customItemMapping || [];
	const items = getItemArrayOfObject();
	const itemsLength = items.length;
	let modifiedItemArray = [];

	log('Items');
	log(items, false);

	for(let i = 0; i < itemsLength; i++) {
		log('Processing item #' + (i+1));
		const overrideMap = convertArrayToObject(itemMapping);
		const modifiedItems = createOverrideObj(items[i], overrideMap);
		log(modifiedItems, false);
		modifiedItemArray.push(modifiedItems);
	}
   return modifiedItemArray;
}

function doAllChecks(payload, scenario) {
    const typeList = ['order', 'visit', 'cart', 'identify', 'custom_event'];

    const caseSelector = typeList.indexOf(payload.type) !== -1 ? payload.type : false;

    log(caseSelector);

    if (caseSelector) {
        log('Testing ' + caseSelector + ' scenario');
        const validationRules = scenario[caseSelector];
        const finalStatus = validatePayload(payload, validationRules);
        return finalStatus;
    } else {
        log('Unsuported scenario');
        return false;
    }
}

function getField(obj, path) {
    const keys = path.split('.');
    let currentObj = obj;

    for (const key of keys) {
        if (currentObj && currentObj.hasOwnProperty(key)) {
            currentObj = currentObj[key];
        } else {
            return undefined;
        }
    }
    return currentObj;
}

function validatePayload(payload, validationRules) {

    const requiredFields = validationRules.required;

    // Check if all top-level required fields are present
    const missingFields = requiredFields.filter(field => {
        const fieldExists = getField(payload, field);
        return !fieldExists;
    });

    if (missingFields.length > 0) {
        log('Missing required fields: ' + missingFields.join(', '));
        return false;
    }

    // Check nested cycles
    if (validationRules.cycle) {

        for (const cycleField in validationRules.cycle) {
            const cycleRules = validationRules.cycle[cycleField];
            const cycleArray = getField(payload, cycleField);

            if (cycleArray && getType(cycleArray) === 'array') {
                // Validate each item in the cycle
                for (const item of cycleArray) {
                    const itemMissingFields = cycleRules.required.filter(field => {
                        function getField(obj, path) {
                            const keys = path.split('.');
                            let currentObj = obj;

                            for (const key of keys) {
                                if (currentObj && currentObj.hasOwnProperty(key)) {
                                    currentObj = currentObj[key];
                                } else {
                                    return undefined;
                                }
                            }
                            return currentObj;
                        }
                        const fieldExists = getField(item, field);
                        return !fieldExists;
                    });

                    if (itemMissingFields.length > 0) {
                        log('Missing required fields in ' + cycleField + ': ' + itemMissingFields.join(', '));
                        return false;
                    }

                    // Check nested cycles within the cycle
                    if (cycleRules.cycle) {
                        const isValidCycle = validatePayload(item, cycleRules);
                        if (!isValidCycle) {
                            return false;
                        }
                    }
                }
            } else {
                log(cycleField + ' is not an array or is missing');
                return false;
            }
        }
    }
    log("Payload validation successful");
    return true;
}

