function sendPayload(outputSchema){
	const collectionAPI = getApiEndpoint();
	const payload = JSON.stringify(outputSchema);
	log('Sending HTTP POST to ' + collectionAPI);
	log(payload);

	sendHttpRequest(collectionAPI, {
		method: 'POST',
		headers: {
			'Content-Type': 'application/json'
		},
		timeout: 5000,
	}, payload).then((result) => {
        log('Server full response');
        log(result, false);
		log("Status: " + result.body.status + " | Message: " + result.body.message);
	});
}

