}

function addAdditionalParams(additionalProperties, object){
  for (let i = 0; i < additionalProperties.length; i++) {
    const property = additionalProperties[i];
    object[property.key] = property.value;
}
}

function clearNullUndefinedEmpty(obj) {
    var cleanedObj = {};
    for (var propName in obj) {
        if (obj[propName] !== null && obj[propName] !== undefined && obj[propName] !== "") {
            cleanedObj[propName] = obj[propName];
        }
    }
    return cleanedObj;
}

function createPayload(){
