const makeString = require('makeString'); 
const makeNumber = require('makeNumber');
const log = require('logToConsole');
const Timestamp = require("getTimestampMillis");
const Math = require('Math');

const currentTimestamp = Math.round(Timestamp() / 1000);
const input = makeString(data.input);
const type = data.type;

var sessionId = 0;
var sessionNumber = 0;

// Detect if it's the new format (has $)
if (input.indexOf('$') !== -1) { 
  const parts = input.split('.');
  const partsTwo = parts[2].split('$');

  // Example: GS2.1.s1747029280$o7$g1$t1747029473$j58$l0$h0
  if (partsTwo.length > 1) {
    sessionId = partsTwo[0].replace('s','');
    sessionNumber = partsTwo[1].replace('o','');
  }

} else {
  // Old format: GS1.1.1745826166.2.0.1745826166.0.0.0
  const parts = input.split('.');
  if (parts.length >= 3) {
    sessionId = parts[2];
    sessionNumber = parts[3];


  }
}

function isSessionValid(currentTimestamp, sessionId){  
  const timeDifference = makeNumber(currentTimestamp) - makeNumber(sessionId);
  const timeDifferenceMinutes = timeDifference / 60;

  return timeDifferenceMinutes < 1440;
}

if (isSessionValid(currentTimestamp, sessionId)){
  return type === "id" ? sessionId : sessionNumber;
} else {
  return undefined;
}


