const decodeUri = require('decodeUri');
const getEventData = require('getEventData');
const parseUrl = require('parseUrl');
const log = require('logToConsole');
const pageLocation = data.pageLocation ? data.pageLocation : getEventData('page_location');
const gclid = data.gclidValue;
const urlInput = parseUrl(pageLocation);


//log(urlInput);
//log(decodeUri(urlInput.href));

if (urlInput) {
    const urlHref = urlInput.href;
    const urlOrigin = urlInput.origin;
    const urlPath = urlInput.pathname;
    const urlSearch = urlInput.search;
    const urlHash = urlInput.hash;
    const urlSplit = urlHref.split("?");
    const hostname = urlSplit[0];
    const urlQueryGclid = urlInput.searchParams.gclid;

    if (gclid) {
        if (urlQueryGclid) {
            return urlHref;
        } else if (urlSearch) {
            //log(fullURL + "&gclid=" + gclid);
            return urlOrigin + urlPath + urlSearch + "&gclid=" + gclid + urlHash;
        } else {
            //log(hostname + "?gclid=" + gclid);
            return urlOrigin + urlPath + "?gclid=" + gclid + urlHash;
        }
    } else {
        return pageLocation;
    }
} else {
    return undefined;
}


