const decodeUri = require('decodeUri');
const getEventData = require('getEventData');
const getTimestampMillis = require('getTimestampMillis');
const parseUrl = require('parseUrl');

const log = require('logToConsole');
const pageLocation = data.pageLocation ? data.pageLocation : getEventData('page_location');
const timestamp = data.timestamp;
const urlInput = parseUrl(pageLocation);
var response;


if (urlInput) {

    const urlQueryFbclid = urlInput.searchParams.fbclid;
    if(urlQueryFbclid){
        if(timestamp){
          response = "fb.1."+timestamp+"."+urlQueryFbclid;
        } else {
          response = "fb.1."+getTimestampMillis()+"."+urlQueryFbclid;
        }
    }  
       return (response) ? response : undefined;




} else {
    return undefined;
}


