const JSON = require('JSON');
const log = require('logToConsole');
const getType = require('getType');

//const createRegex = require('createRegex');
//const openSymbolsRegex = createRegex('[\{\[]', 'g');
//const closingSymbolsRegex = createRegex('[\}\]]', 'g');
var json_value = data.text;




var stringified_json_value = JSON.stringify(json_value);
var beautified_json_in_string = '';
const value_type = getType(stringified_json_value);
if(value_type === 'string'){
  var length = stringified_json_value.length;
  var gap = '';
  var nl = '\n';
  for(var i=0; i < length; i++){
      var current_char = stringified_json_value[i];

      if((current_char == '{') || (current_char == '[')){
     //   log('open');
        gap = gap + '\t';
        beautified_json_in_string += current_char + nl + gap; 

     //   log(JSON.stringify(gap));

      }else if ((current_char == '}') || (current_char == ']')){
       // log('close');
        gap = gap.slice(1);
        beautified_json_in_string += current_char +  nl + gap; 

      //  log(JSON.stringify(gap));

      }else if ((current_char == ',')){
        beautified_json_in_string += current_char + nl + gap; 


      }else if((current_char == '&')){ //slack needs to convert these three chars to HTML entity
        beautified_json_in_string += '&amp';

      }else if((current_char == '<')){
        beautified_json_in_string += '&lt;';
      }else if((current_char == '>')){
        beautified_json_in_string += '&gt;';

      }else {
        beautified_json_in_string += current_char;
      }

  }


}
/*

var json_final = stringified_json_value.split('{').join('{\n').split('[').join('[\n').split(',').join(',\n').split(']').join(']\n').split('}').join('}\n');
log(json_final);
return (json_final) ? json_final : undefined;

*/



return (beautified_json_in_string) ?  JSON.stringify(beautified_json_in_string) : undefined;


