const log = require('logToConsole');
const decodeUriComponent = require('decodeUriComponent');
const createRegex = require('createRegex');
const getType = require('getType');
const JSON = require('JSON');


var items = data.items;
var protocol = data.mpVersion;
var identifier = data.itemIdentificator;
var removeValue = data.removeValue;
var response = [];
var category;

const regex = createRegex('[a-zA-Z]*', 'g');

//log(items);
//log(identifier);
if (items && identifier) {

    for (var i = 0; i < items.length; i++) {
      category = undefined;
      if(protocol == 'mpv2'){
        if(items[i].item_category){
          category = items[i].item_category;
        }
        if(items[i].item_category2){
          category += " | "+items[i].item_category2;
        }
        if(items[i].item_category3){
          category += " | "+items[i].item_category3;
        }
        if(items[i].item_category4){
          category += " | "+items[i].item_category4;
        }
        if(items[i].item_category5){
          category += " | "+items[i].item_category5;
        }
      } else if (protocol == 'mpv1'){
        if(getType(items[i].item_category) == 'string'){
          var splittedCategory = items[i].item_category.split('/');
          category = splittedCategory.join(' | ');
        }
      }


      if (items[i][identifier] && items[i].quantity) {
        if(data.removeCharsFromID != 'false'){
          if(getType(items[i][identifier]) == 'string'){
           if(items[i][identifier].indexOf('-') == -1){ //dont do if this is the new one id
                     log('here '+items[i][identifier]);

            items[i][identifier] = (items[i][identifier]).replace(regex, '');
           }
          } else if(getType(items[i][identifier]) == 'number'){
             items[i][identifier] = JSON.stringify(items[i][identifier]);
          }
        }

        response.push({
          'content_id': (items[i][identifier]) ? items[i][identifier] : undefined,
          'content_name': (items[i].item_name) ? items[i].item_name : undefined,
          'quantity': (items[i].quantity) ?  items[i].quantity : undefined,
          'content_category': (category) ? category : undefined,
          'brand': (items[i].item_brand) ? items[i].item_brand : undefined,


        });
        //  log(i +':'+ {'id' : items[i][identifier], 'quantity' : items[i].quantity  });
      }

    }
  }

  // Variables must return a value.
  return (response) ? response : [];


