const JSON = require('JSON');
const makeTableMap = require('makeTableMap');
const Object = require('Object');
const getType = require('getType');
const log = require('logToConsole');

function sendResponse () {
  const rq_method = getRequestMethod();

  setResponseHeader('Access-Control-Allow-Origin', getRequestHeader('origin'));
  setResponseHeader('Access-Control-Allow-Credentials', 'true');

  if (rq_method === "GET") {
    require('setPixelResponse')();
  }

  returnResponse();
}

function processEvents(event_data) {
  var common_params = {};
  common_params.user_agent = getRequestHeader('user-agent');
  common_params['x-ga-protocol_version'] = 2;
  common_params['x-ga-gcs'] = 'G111';

  for (let key in event_data) {
    if(key === 'events'){ continue; }

    common_params[key] = event_data[key];
  }

  runContainer(common_params);
}
const paths = makeTableMap(data.paths_whitelist || [], "path", "path") || {};
const rq_path = require('getRequestPath')();
if(getType(paths[rq_path]) === "undefined"){ return; }

require('claimRequest')();
sendResponse();

const request_data = getRequestQueryParameters(); 
log("received data:", request_data);
processEvents(request_data);


