const decodeUri = require('decodeUri');
const getEventData = require('getEventData');
const parseUrl = require('parseUrl');
const getType = require('getType');
const createRegex = require('createRegex');
const JSON = require('JSON');
const encodeUriComponent = require('encodeUriComponent');

const log = require('logToConsole');
const pageLocation = data.pageLocation ? data.pageLocation : getEventData('page_location');

const urlInput = parseUrl(pageLocation);
log(urlInput);

var response = urlInput.origin+urlInput.pathname;      //No query parameters
const searchParams = urlInput.searchParams || {}; // The current query parameters object


const rules = data.parameters_table || [];    // Array of rules to apply

let ruleMap = {};
rules.forEach(function(rule) {
  if (rule.queryParamName && rule.action) {
    ruleMap[rule.queryParamName] = rule.action;
  }
});



let queryStringParts = [];

for (let key in searchParams) {
  if (!searchParams.hasOwnProperty(key)) continue;

  let value = searchParams[key];
  let action = ruleMap[key];

  if (action === 'remove_whole_parameter') {
    // Do not include this param at all
    continue;
  } else if (action === 'remove_value') {
    //mark its value as REMOVED
    queryStringParts.push((encodeUriComponent(key)) + '=REMOVED');
  } else {
    // Normal key-value pair
    queryStringParts.push(
      encodeUriComponent(key) + '=' + encodeUriComponent(value)
    );
  }
}

response += queryStringParts.length ? '?' + queryStringParts.join('&') : '';
response += urlInput.hash.length ? urlInput.hash : '';

return response;


